pragma solidity ^0.5.0;

contract Adoption {
  address[] public adopters;

  struct Pet {
      uint id;
      string name;
      string picture;
      uint age;
      string breed;
      string location;
  }

  Pet[] public pets;


  function registerPet(string memory name, string memory breed, uint age, string memory location, string memory picture) public {
      pets.push(Pet(pets.length, name, picture, age, breed, location));
      adopters.push(address(0));
  }

  function getPetsCount() public view returns (uint) {
      return pets.length;
  }

  // Additional function to get pet details by index to facilitate displaying pets on the homepage
  function getPet(uint index) public view returns (uint, string memory, string memory, uint, string memory, string memory) {
      require(index < pets.length, "Pet does not exist.");
      Pet memory pet = pets[index];
      return (pet.id, pet.name, pet.picture, pet.age, pet.breed, pet.location);
  }

  // Adopting a pet
  function adopt(uint petId) public returns (uint) {
    // require(petId >= 0 && petId <= 15, "Pet ID must be within range 0 to 15.");

    adopters[petId] = msg.sender;


    return petId;
  }

  // Unadopting a pet
  function unadopt(uint petId) public returns (uint) {
    // require(petId >= 0 && petId <= 15, "Pet ID must be within range 0 to 15.");

    adopters[petId] = address(0) ;

    // Emitting event after successful adoption
    return petId;
  }

  // Retrieving the adopters
  function getAdopters() public view returns (address[] memory) {
    return adopters;
  }

}
